import type { ItineraryLocale } from "@/lib/i18n/itinerary";

export type DayPlan = {
  day: number;
  title: string;
  summary: string;
  morning: string;
  afternoon: string;
  evening: string;
};

export type LogisticsItem = {
  label: string;
  value: string;
};

export type FaqItem = {
  question: string;
  answer: string;
};

export type RelatedItinerary = {
  slug: string;
  city: string;
  days: number;
  description: string;
};

export type CityItinerary = {
  slug: string;
  city: string;
  country: string;
  days: number;
  pace: string;
  idealFor: string[];
  style: string[];
  pacing?: string[];
  hero: {
    title: string;
    subtitle: string;
    eyebrow?: string;
    image?: {
      src: string;
      alt: string;
    };
  };
  cityStats?: Array<{
    value: string;
    label: string;
  }>;
  fit: {
    forYou: string[];
    notForYou: string[];
  };
  dayPlans: DayPlan[];
  imageInfoCards?: Array<{
    title: string;
    description: string;
    image: {
      src: string;
      alt: string;
    };
  }>;
  logistics: LogisticsItem[];
  goodToKnow?: LogisticsItem[];
  checklist: string[];
  faqs: FaqItem[];
  relatedItineraries: RelatedItinerary[];
  primaryCtaHref: string;
  secondaryCtaHref?: string;
};

